// npm install razorpay --save :-library provides payment gateway.
// payement gattway is used to imployement online payment functionality.

import React,{useEffect,useState} from "react";
import axios from "axios";
import logo from "../logo.svg"
import "../index.css";

function Bill(props)
{
    const[mydate,setMyDate]=useState();
    const[custdata,setCustData]=useState();
    const[cname,setCName]=useState();
    const[caddress,setCAddress]=useState();
    const[ccontact,setCContact]=useState();
    const[sitems,setSItem]=useState([]);
    var total=0;
    var nextbillid="";
    const[ispaymentdon,setisPayemntDone]=useState(false);
    useEffect(()=>{
        //alert("props length="+props.data.selitems.length);
        for(var i=0;i<props.data.selitems.length;i++)
        {
            sitems.push(props.data.selitems[i]);
        }
        //alert("Item count in sitem="+sitems.length);
        //alert("Customer ID"+props.data.cid)
        axios.get("http://localhost:9292/customer/getcustomerdetails/"+props.data.cid).then((res)=>{
            setCName(res.data.CustomerName);
            setCAddress(res.data.CAddress);
            setCContact(res.data.CContact);
            mydateFun();
        }).catch((err)=>{
            // alert(err,"error");
            alert("error");
        })
    },[]);
    function mydateFun()
    {
        const date=new Date();
        let day=date.getDate();
        let month=date.getMonth()+1;
        let year=date.getFullYear();
        let currentDate=`${day}-${month}-${year}`;
        console.log(currentDate);
        setMyDate(currentDate);
    }
    //const[selitems,setSelItems]=useState([]);
    //var total=0;
    function loadScript(src)
    {
        return new Promise((resolve)=>{
            const script=document.createElement("script");
            script.src=src;
            script.onload=()=>{
                resolve(true);
            };
            script.onerror=()=>{
                resolve(false);
            };
            document.body.appendChild(script);
        });
    }
    function SaveBill()
    {
                alert("done")

        //alert(sitems.length);
        var nextbillid="";
        axios.get("http://localhost:9292/bill/getbillid").then((res)=>{
            console.log(res);
            //res.data=res.data||1
            if(res.data.length===0){
                nextbillid=1;
            }else{
            nextbillid=parseInt(res.data[0].billid)+1;
            }
            console.log(nextbillid);
            
           

        const date=new Date();
        let day=date.getDate();
        let month=date.getMonth()+1;
        let year=date.getFullYear();
        let currentDate=`${day}-${month}-${year}`;
        console.log(sitems);
        
        sitems.map((item)=>{
            // alert(item.pid)
            var billobj={
                billid:nextbillid,
                billdate:currentDate,
                cid:props.data.cid,
                pid:item.pid
            }
            console.log(billobj);
            
            axios.post("http://localhost:9292/bill/billsave",billobj).then((res)=>{
                // alert(res.data);
                alert("done")
            })
        });
        //.catch((err)=>{
            //alert("inner"+err);
          //  });
        }).catch((err)=>{
            alert("failed");
            // alert("outer"+err);
        })
    }
    async function displayRazorpay(){
        if(ispaymentdon==true)
        {
            alert("Payment Already Done");
            return;
        }
        else{
            SaveBill();
            const res=await loadScript(
                "https://checkout.razorpay.com/v1/checkout.js"
            );
            if(!res){
                alert("Razorpay SDK failed to load. Are you online?");
                return;
            }
            var myamount=total+100;
            console.log(myamount);
            
            //creating a new order
            const result=await axios.post("http://localhost:9292/payment/orders/"+myamount);
            if(!result){
                alert("Server error. Are you online?");
                return;
            }
            //Getting the order details back
            const{amount,id:order_id,currency}=result.data;

            const options={
                key:"rzp_text_BCxHBNuMQt1Qn8",
                amount:amount.toString(),
                currency:currency,
                name:"Universal Informatics Pvt. Ltd. Indore",
                description:"Test Transaction",
                image:{logo},
                order_id:order_id,
                handler:async function(response) {
                    const data={
                        orderCreationId:order_id,
                        razorpayPaymentId:response.razorpay_payment_id,
                        razorpayOrderId:response.razorpay_order_id,
                        razorpaySignature:response.razorpay_signature,
                    };
                    alert(data.razorpayPaymentId)
                    const result=await axios.post("http://localhost:9292/payment/success",data);
                    alert("Message From Payment Gateway"+result.data);
                    //save payment details

                    const paydetlobjdata={
                        orderCreationId:order_id,
                         razorpayPaymentId:response.razorpay_payment_id,
                        razorpayOrderId:response.razorpay_order_id,
                        razorpaySignature:response.razorpay_signature,
                        cid:props.data.cid,
                        billid:nextbillid,
                        amount:amount/100
                    };
                    axios.post("http://localhost:9292/paymentdetails/paymentdetailsave",paydetlobjdata).then((res)=>{
                        alert(res.data);
                        if(res.data=="payment details saved successfully")
                        {
                            //alert("Payment Already Done");
                            setisPayemntDone(true);
                        }
                    }).catch((err)=>{
                        // alert(err,"failed");
                        alert("failed");
                    })
                },
                prefill:{
                    name:"Universal Informatics",
                    email:"universal@gmail.com",
                    contact:"9999999999",
                },
                notes:{
                    address:"Universal Informatics Indore pvt. Ltd.",
                },
                theme:{
                    color:"#61dafb",
                },
            };
            const paymentObject=new window.Razorpay(options);
            paymentObject.open();
        }
    }
    return(
        <div className="container">
            <center>
                <h2 style={{backgroundColor:"green"}}>Invoice/Bill</h2>
            </center>
            <div className="form-container">
                <h4>Customer Details</h4>

                <table>
                    <tr>
                        <td>Customer Id</td>
                        <td>{props.data.cid}</td>
                    </tr>
                    <tr>
                        <td>Customer Name</td>
                        <td>{cname}</td>
                    </tr>
                    <tr>
                        <td>Address</td>
                        <td>{caddress}</td>
                    </tr>
                    <tr>
                        <td>Contact</td>
                        <td>{ccontact}</td>
                    </tr>
                    <tr>
                        <td>Bill Date</td>
                        <td>{mydate}</td>
                    </tr>
                </table>
            </div>
            <div className="form-container">
                <h4>Item Details</h4>
                <table border="2">
                    <tr style={{backgroundColor:"gray"}}>
                        <th>ID</th>
                        <th>Product Name</th>
                        <th>Price</th>
                        <th>Photo</th>
                    </tr>
                    {
                        props.data.selitems.map((item)=>(
                            <tr>
                                <td>{item.pid}</td>
                                <td>{item.pname}</td>
                                <td>{item.oprice}</td>
                                <img src={"http://localhost:9292/product/getproductimage/"+item.ppicname} height="50" width="50"/>
                            </tr>
                        ))
                    }
                </table>
            </div>
            <div className="form-container">
                {
                    props.data.selitems.map((item)=>{
                        total=total+item.oprice;
                    })
                }
                <h2 style={{backgroundColor:"green"}}>Total Amount={total}</h2>
                <center>
                    <button type="submit" onClick={displayRazorpay}>Pay Now</button>
                </center>
            </div>
        </div>
    );
}export default Bill;
