import React,{useState,useEffect} from "react";
import axios from "axios";
function Product(props)
{
    const[pid,setPId]=useState();
    const[pname,setPName]=useState();
    const[pprice,setPPrice]=useState();
    const[oprice,setOPrice]=useState();
    const[ppicname,setPPicName]=useState();
    const[pcatgid,setPCatgId]=useState();
    const[pcatglist,setPCatgList]=useState([]);
    const[image,setImage]=useState({preview:'',data:''});
    const[status,setStatus]=useState('');
    const[plist,setPList]=useState([]);
    var cname="";
    var catgname="";

    var venderid=props.data==undefined?0:props.data;

    const handlePidText=(evt)=>{
        setPId(evt.target.value);
    }
    const handlePNameText=(evt)=>{
        setPName(evt.target.value);
    }
    const handlePPriceText=(evt)=>{
        setPPrice(evt.target.value);
    }
    const handleOPriceText=(evt)=>{
        setOPrice(evt.target.value);
    }
    const handlePCatgSelect=(evt)=>{
        setPCatgId(evt.target.value);
    }
    useEffect(()=>{
        //var venderid=props.data==undefined?0:props.data;
        alert("VID="+venderid);
        axios.get("http://localhost:9292/product/getmaxpid").then((res)=>{
            setPId(res.data.length+1);
        }).catch((err)=>{
            alert(err);
        });
        axios.get("http://localhost:9292/productcatg/showproductcatg").then((res)=>{
            setPCatgList(res.data);
        }).catch((err)=>{
            alert(err);
        });
    },[]);
    const handleSaveButton=()=>{
        var obj={
            pid:pid,
            pname:pname,
            pprice:pprice,
            oprice:oprice,
            ppicname:ppicname,
            pcatgid:pcatgid,
            vid:venderid,
            status:"Active"
        };
        axios.post("http://localhost:9292/product/saveproduct/",obj).then((res)=>{
            alert("Product Saved");
        }).catch((err)=>{
            alert(err);
        });
    }
    const handleShowButton=()=>{
        axios.get("http://localhost:9292/product/showproductbyvender/"+venderid).then((res)=>{
            setPList(res.data);
        }).catch((err)=>{
            alert(err);
        });
    }
    //browse and save image code
    const handleSubmit=async (evt)=>{
        evt.preventDefault()
        let formData=new FormData()
        formData.append('file',image.data);
        const response=await fetch('http://localhost:9292/product/saveproductimage',{
            method:'POST',
            body:formData,
        })
        if(response) {
            if(response.statusText=="ok")
            {
                setStatus("File Uploaded Successfully");
            }else{
                setStatus("Failed to Upload File");
            }
        }
    }
    const handleFileChange=(evt)=>{
        const img={
            preview:URL.createObjectURL(evt.target.files[0]),
            data:evt.target.files[0]
        }
        setImage(img)
        setPPicName(evt.target.files[0].name);
    }
    const handleNewButton=()=>{
        axios.get("http://localhost:9292/product/getmaxpid").then((res)=>{
            setPId(res.data.length+1);
            setPName("");
            setPCatgId("");
            setPPrice("");
            setOPrice("");
            setPPicName("");
            setImage("");
        }).catch((err)=>{
            alert(err);
        });
    }
    return(
        <div>
            <center>
                <p>Vender Id {venderid}</p>
                <p style={{backgroundColor:"green",color:"white"}}>Product Form</p>
                <div className="jumbotron" style={{marginLeft:20,marginRight:20,borderRadius:5}}>
                    <table>
                        <tr>
                            <td>Product Id</td>
                            <td>{pid}</td>
                        </tr>
                        <tr>
                            <td>Product Name</td>
                            <td>
                                <input type="text" onChange={handlePNameText} value={pname}/>
                            </td>
                        </tr>
                        <tr>
                            <td>Price</td>
                            <td>
                                <input type="number" onChange={handlePPriceText} value={pprice}/>
                            </td>
                        </tr>
                        <tr>
                            <td>Offer Price</td>
                            <td>
                                <input type="number" onChange={handleOPriceText} value={oprice}/>
                            </td>
                        </tr>
                        <tr>
                            <td>Select Photo</td>
                            <td>
                                <input type="file" onChange={handleFileChange} name="file"/>
                                <img src={image.preview} width='100' height='100'/>
                            </td>
                        </tr>
                        <tr>
                            <td>Click To Upload Product Photo</td>
                            <td>
                                <button type="submit" onClick={handleSubmit}>Upload</button>
                            </td>
                        </tr>
                        <tr>
                            <td>Category</td>
                            <td>
                                <select onClick={handlePCatgSelect}>
                                    {
                                        pcatglist.map((item)=>(
                                            <option value={item.pcatgid}>{item.pcatgname}</option>
                                        ))
                                    }
                                </select>
                            </td>
                        </tr>
                        <tr>
                            <td>
                                <button type="submit" onClick={handleNewButton}>New</button>
                            </td>
                        </tr>
                        <tr>
                            <td>
                                <button type="submit" onClick={handleSaveButton}>Save</button>
                            </td>
                        </tr>
                        <tr>
                            <td>
                                <button type="submit" onClick={handleShowButton}>Show</button>
                            </td>
                        </tr>
                    </table>
                </div>
                <p style={{backgroundColor:"green",color:"white"}}>Product List</p>
                <div className="jumbotron" style={{marginLeft:20,marginRight:20,borderRadius:5}}>
                    <table border={0}>
                        <tr>
                            <th>SNO</th>
                            <th>Product Id</th>
                            <th>Product Name</th>
                            <th>Price</th>
                            <th>Offer Price</th>
                            <th>Category Name</th>
                            <th>Photo</th>
                        </tr>
                        {
                            plist.map((item,index)=>(
                                <tr>
                                    <td>{index+1}</td>
                                    <td>{item.pid}</td>
                                    <td>{item.pname}</td>
                                    <td>{item.pprice}</td>
                                    <td>{item.oprice}</td>
                                    <td>
                                        {
                                            pcatglist.map((citem)=>{
                                                if(item.pcatgid==citem.pcatgid)
                                                {
                                                    cname=(citem.pcatgname)
                                                }
                                            })
                                        }
                                        {cname}
                                    </td>
                                    <td>
