import React,{useEffect,useState} from "react";
import axios from "axios";
import ReactDOM from "react-dom/client";
import Bill from "../customerviews/Bill";

function ProductList(props) {
    const[itemcount,setItemCount]=useState(0);
    const[selitems,setSelItems]=useState([]);
    const[pcatglist,setPCatgList]=useState([]);
    const[plist,setPList]=useState([]);
    const[vlist,setVList]=useState([]);

    var cname="";

    useEffect(()=>{
        axios.get("http://localhost:9292/product/showproduct").then((res)=>{
            setPList(res.data);
        }).catch((err)=>{
            alert(err);
        });
        axios.get("http://localhost:9292/productcatg/showproductcatg").then((res)=>{
            setPCatgList(res.data);
        }).catch((err)=>{
            alert(err);
        });
        //get vender
        axios.get("http://localhost:9292/vender/getvendercount").then((res)=>{
            setVList(res.data);
        }).catch((err)=>{
            alert(err);
        });
    },[]);

    const handleActiveButton=(evt)=>{
        var pid=parseInt(evt);
        var status="Active";
        axios.put("http://localhost:9292/product/updateproductstatus/"+pid+"/"+status).then((res)=>{
            alert("Product Status Updated");
        }).catch((err)=>{
            alert(err);
        });
    }
    const handleInactiveButton=(evt)=>{
        var pid=parseInt(evt);
        var status="Inactive";
        axios.put("http://localhost:9292/product/updateproductstatus/"+pid+"/"+status).then((res)=>{
            alert("Product Status Updated");
        }).catch((err)=>{
            alert(err);
        });
    }
    const handleCheckOutButton=()=>{
        alert("Hello");
        if(selitems.length<=0)
        {
            alert("Please Buy Some Product");
        }
        else{
            const root=ReactDOM.createRoot(document.getElementById("root"));
            var ccid=props.data;
            var obj={
                selitems:selitems,
                cid:ccid
            };
            root.render(<Bill data={obj}></Bill>)
        }
    }
    const handleSearch=(evt)=>{
        if(evt.target.value>0)
        {
            axios.get("http://localhost:9292/product/showproductbycatgid/"+evt.target.value).then((res)=>{
            setPList(res.data);
        }).catch((err)=>{
            alert(err);
        });
        }else{
            axios.get("http://localhost:9292/product/showproduct").then((res)=>{
            setPList(res.data);
        }).catch((err)=>{
            alert(err);
        });
        }
    }
    const handleSearchByVender=(evt)=>{
        if(evt.target.value>0)
        {
            axios.get("http://localhost:9292/product/showproductbyvender/"+evt.target.value).then((res)=>{
            setPList(res.data);
        }).catch((err)=>{
            alert(err);
        });
        }else{
            axios.get("http://localhost:9292/product/showproduct").then((res)=>{
            setPList(res.data);
        }).catch((err)=>{
            alert(err);
        });
        }
    }
    return(
        <div className="container">
            <center>
                Search By Category <select onClick={handleSearch}>
                    <option value="0">All</option>
                    {
                        pcatglist.map((pcatgitem)=>(
                            <option value={pcatgitem.pcatgid}>{pcatgitem.pcatgname}</option>
                        ))
                    }
                </select>
                <p>
                    Search By Vender<select onClick={handleSearchByVender}>
                    <option value="0">All</option>
                    {
                        vlist.map((vitem)=>(
                            <option value={vitem.vid}>{vitem.VenderName}</option>
                        ))
                    }
                </select>
                </p>
                <h4 style={{backgroundColor:" rgb(120, 106, 133)",color:"black"}}>Product List</h4>
                <div className="form-container">
                    <table border={1}>
                        <tr>
                            <th>Id</th>
                            <th>Product Name</th>
                            <th>Price</th>
                            <th>Offer Price</th>
                            <th>Category Name</th>
                            <th>Photo</th>
                            <th>Status</th>
                            <th>Action</th>
                        </tr>
                        {
                            plist.map((item)=>(
                                <tr>
                                    <td>{item.pid}</td>
                                    <td>{item.pname}</td>
                                    <td>{item.pprice}</td>
                                    <td>{item.oprice}</td>
                                    <td>
                                        {
                                            pcatglist.map((citem)=>{
                                                if(item.pcatgid==citem.pcatgid)
                                                {
                                                    cname=(citem.pcatgname)
                                                }
                                            })
                                        }
                                        {cname}
                                    </td>
                                    <td>
                                        <img src={"http://localhost:9292/product/getproductimage/"+item.ppicname} height="100" width="100"/>
                                    </td>
                                    <td>{item.status}</td>
                                    <td>
                                        <button type="submit" onClick={()=>handleActiveButton(item.pid)}>Active</button><span></span><button type="submit" onClick={()=>handleInactiveButton(item.pid)}>Inactive</button>
                                    </td>
                                </tr>
                            ))
                        }
                    </table>
                </div>
            </center>
        </div>
    );

}export default ProductList;
