//ProductCatg.js
import React,{useEffect,useRef,useState} from "react";
import axios from "axios";

function ProductCatg()
{
    const pcatgid=useRef();
    const pcatgname=useRef();
    const[pcatglist,setPCatgList]=useState([]);


    const handleSaveButton=()=>{
        var Pcatgid=pcatgid.current.value;
        var Pcatgname=pcatgname.current.value;

        axios.post("http://localhost:9292/productcatg/addproductcatg/"+Pcatgid+"/"+Pcatgname).then(((res)=>{
            alert(res.data);
        })).catch((err)=>{
            alert(err);
        })
    }
    useEffect(()=>{
        axios.get("http://localhost:9292/productcatg/showproductcatg").then((res)=>{
            setPCatgList(res.data);
        }).catch((err)=>{
            alert(err);
        })
    },); //-> it will live or directly update //  },[]);-> it will load new product after refresing the page
    return(
        <div>
            <center>
                <h4 style={{backgroundColor: rgb,color:"black"}}>Product Category Management </h4>
                <table>
                    <tr>
                        <td>Category Id</td>
                        <td>
                            <input type="number" ref={pcatgid}/>
                        </td>
                    </tr>
                    <tr>
                        <td>Category Name</td>
                        <td>
                            <input type="text" ref={pcatgname}/>
                        </td>
                    </tr>
                    <tr>
                        <td></td>
                        <td>
                            <button type="submit" onClick={handleSaveButton}>Save</button>
                        </td>
                    </tr>
                </table>
                <h4>Category List</h4>
                <table>
                    <tr>
                        <th>Category Id</th>
                        <th>Category Name</th>
                    </tr>
                    {
                        pcatglist.map((item)=>(
                            <tr>
                                <td>{item.pcatgid}</td>
                                <td>{item.pcatgname}</td>
                            </tr>
                        ))
                    }
                </table>
            </center>
        </div>
    );
}export default ProductCatg;
