import React,{useRef} from "react";
import axios from "axios";

function ProductCatg()
{
    const pcatgid=useRef();
    const pcatgname=useRef();

    const handleSaveButton=()=>{
        var Pcatgid=pcatgid.current.value;
        var Pcatgname=pcatgname.current.value;
        axios.post("http://localhost:9292/productcatg/addproductcatg/"+pcatgid+"/"+pcatgname).then(((res)=>{
            alert(res.data);
        })).catch((err)=>{
            alert(err);
        })
    }
    return(
        <div>
            <center>
                <h4 style={{backgroundColor:"yellow",color:"white"}}>Product Category Management </h4>
                <table>
                    <tr>
                        <td>Category Id</td>
                        <td>
                            <input type="number" ref={pcatgid}/>
                        </td>
                    </tr>
                    <tr>
                        <td>Category Name</td>
                        <td>
                            <input type="text" ref={pcatgname}/>
                        </td>
                    </tr>
                    <tr>
                        <td></td>
                        <td>
                            <button type="submit" onClick={handleSaveButton}>Save</button>
                        </td>
                    </tr>
                </table>
            </center>
        </div>
    );
}export default ProductCatg;
